import React, { useState} from "react";
import {Form, Button } from "react-bootstrap";

const RegistrarUsuarios = () => {

/*   const [datos, setDatos] = useState({
    password: '',
    nombre: '',
    apellidopaterno: '',
    apellidomaterno: ''
}) */
    const [password, setPassword] = useState("");
    const [nombre, setNombre] = useState("");

/*   const handleInputChange = (event) => {
     // console.log(event.target.name)
     // console.log(event.target.value) 
    setDatos({
        ...datos,
        [event.target.name] : event.target.value
    })
} */

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      /* console.log(datos); */
      const body = { password, nombre };
      const response = await fetch("http://localhost:5000/usuarios", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      console.log(response);
      /* window.location = "/usuarios/"; */
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <>
      <div className="contenedor">
        <Form onSubmit={onSubmitForm}>

            {/* <Form.Label>Nombre</Form.Label> */}
{/*           <Form.Group className="mb-4" controlId="formNombre">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Nombre"
              name="nombre"
              onChange={handleInputChange} 
            />
          </Form.Group>

          <Form.Group className="mb-4" controlId="formPaterno">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Apellido paterno"
              name="apellidopaterno"
              onChange={handleInputChange} 
            />
          </Form.Group>

        */}
          <Form.Group className="mb-4" controlId="formMaterno">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Apellido materno"
              value={nombre}
              onChange={e => setNombre(e.target.value)} 
            />
          </Form.Group>
 
          <Form.Group className="mb-3" controlId="formPass">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)} 
            />
            <Form.Text className="text-muted">
              Asegurese ingresar correctamente la contrase√±a
            </Form.Text>
          </Form.Group>

          <br />

          <Button variant="primary" type="submit">
            Agregar
          </Button>
        </Form>
      </div>
    </>
  );
};

export default RegistrarUsuarios;
