import React, { useState} from "react";
import {Form, Button } from "react-bootstrap";

const RegistrarUsuarios = () => {

  const [datos, setDatos] = useState({
    pass: '',
    nombre: '',
    paterno: '',
    materno: ''
})

  const handleInputChange = (event) => {
    // console.log(event.target.name)
    // console.log(event.target.value)
    setDatos({
        ...datos,
        [event.target.name] : event.target.value
    })
}

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { datos };
      const respuesta = await fetch("http://localhost:5000/usuarios", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(body),
      });
      console.log(respuesta);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <>
      <div className="contenedor">
        <Form onSubmit={onSubmitForm}>

          <Form.Group className="mb-4" controlId="formNombre">
            {/* <Form.Label>Nombre</Form.Label> */}
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Nombre"
              name="nombre"
              onChange={handleInputChange} 
            />
          </Form.Group>

          <Form.Group className="mb-4" controlId="formPaterno">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Apellido paterno"
              name="paterno"
              onChange={handleInputChange} 
            />
          </Form.Group>

          <Form.Group className="mb-4" controlId="formMaterno">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Apellido materno"
              name="materno"
              onChange={handleInputChange} 
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formPass">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Password"
              name="pass"
              onChange={handleInputChange} 
            />
            <Form.Text className="text-muted">
              Asegurese ingresar correctamente la contrase√±a
            </Form.Text>
          </Form.Group>

          <br />

          <Button variant="primary" type="submit">
            Agregar
          </Button>
        </Form>
      </div>
    </>
  );
};

export default RegistrarUsuarios;
