import React, { useState } from 'react'
import { Table, Tabs, Tab, Form, Button } from "react-bootstrap";

import "./usuarios.scss";

const Usuarios = () => {

    const [key, setKey] = useState('Registrar');

    /* agregar usuario */

    const [usuario, setUsuario] = useState('');
    
    const onSubmitForm = async(e) => {
      e.preventDefault();
      try {
      const body = {usuario};
      const respuesta =
      await fetch("http://localhost:5000/usuarios",{
        method:"POST",
        headers:{"Content-type":"application/json"},
        body: JSON.stringify(body)
        });
        console.log(respuesta);
      } catch (err){
      console.error(err.message);
    }
  }
  
  /* para listar los usuarios  */
  const [editUsuario, setEditUsuario] = useState([]);
  async function obtenerUsuario() {
    const res = await fetch("http://localhost:5000/usuarios");
    const usuariosArray = await res.json();
    setEditUsuario(usuariosArray) 
  }

  useEffect(() => {
      obtenerUsuario();
  }, []);
  console.log(editUsuario);

    
    return (
        <>
        <div className="usuarios">
          <div className="container">
            <div className="usuarios__titulo">
              <h2>Usuarios</h2>
            </div>
            <Tabs
              id="controlled-tab-example"
              activeKey={key}
              onSelect={(k) => setKey(k)}
              className="mb-2 w-100"
              style={{textDecoration:"none"}}
            >
              <Tab eventKey="Registrar" title="Registrar">
  
              <div className="contenedor">
              <Form onSubmit={onSubmitForm}>
                  
                      <Form.Group className="mb-3" controlId="formNombre">
                          <Form.Label>Nombre</Form.Label>
                          <Form.Control
                            autoComplete="off" 
                            type="text"
                            placeholder="Nombre completo"
                            value={usuario}
                            onChange={e => setUsuario(e.target.value)}
                          />
                      </Form.Group>

                      <Button variant="primary" type="submit">
                          Agregar
                      </Button>
                  </Form>
                  </div>
  
              </Tab>
  
              <Tab eventKey="Revisar y buscar" title="Revisar y buscar">
  
              <Table striped bordered hover>
                  <thead>
                    <tr>
                      <th>Nombre</th>
                      <th>Editar</th>
                      <th>Eliminar</th>
                    </tr>
                  </thead>
                  <tbody>
                    { editUsuario.map( usuario =>(
                    <tr>
                      <td>1</td>
                      <td>{usuario.nombre}</td>
                      <td><Button className="btn btn-danger">Eliminar</Button></td>
                      <td>Editar</td>
                    </tr>
                    ))}
                  </tbody>
                </Table>
  
              </Tab>
  
            </Tabs>
          </div>
        </div>
      </>
    )
}

export default Usuarios
