import React, { useState} from "react";
import {Form, Button } from "react-bootstrap";

const RegistrarUsuarios = () => {

  const [nombre, setNombre] = useState("");
  const [paterno, setPaterno] = useState("");
  const [materno, setMaterno] = useState("");
  const [pass, setPass] = useState("");

  const usuario = {
    pass,
    nombre,
    paterno,
    materno,
  };

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { usuario };
      const respuesta = await fetch("http://localhost:5000/usuarios", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(body),
      });
      console.log(respuesta);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <>
      <div className="contenedor">
        <Form onSubmit={onSubmitForm}>
          <Form.Group className="mb-4" controlId="formNombre">
            {/* <Form.Label>Nombre</Form.Label> */}
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Nombre"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
            />
          </Form.Group>

          <Form.Group className="mb-4" controlId="formPaterno">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Apellido paterno"
              value={paterno}
              onChange={(e) => setPaterno(e.target.value)}
            />
          </Form.Group>

          <Form.Group className="mb-4" controlId="formMaterno">
            <Form.Control
              autoComplete="off"
              type="text"
              placeholder="Apellido materno"
              value={materno}
              onChange={(e) => setMaterno(e.target.value)}
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formPass">
            <Form.Control
              autoComplete="off"
              type="password"
              placeholder="Password"
              value={pass}
              onChange={(e) => setPass(e.target.value)}
            />
            <Form.Text className="text-muted">
              Asegurese ingresar correctamente la contrase√±a
            </Form.Text>
          </Form.Group>

          <br />

          <Button variant="primary" type="submit">
            Agregar
          </Button>
        </Form>
      </div>
    </>
  );
};

export default RegistrarUsuarios;
